mapper.insert:
insert	into category (title, description, blog_id)
values	(
	#{title}
	, #{description}
	, #{blogId}
);

	select	last_insert_id();

mapper.update:
update	category
   set	use_yn = false
 where	no=#{no}
   and	blog_id=#{blogId}
;

mapper.select:
select	no, title, description, reg_date, use_yn, blog_id
  from	category
 where	blog_id=#{blogId}
   and	use_yn=true
 order	by reg_date asc
;

select	count(*)
  from	category
 where	blog_id=#{blogId}
   and	use_yn=true
 order	by reg_date asc
;

select	count(*)
  from	category
 where	no=#{no}
   and	use_yn=true
   and	blog_id=#{blogId}
;
